mapper.update:
		  UPDATE sb_board
		  SET b_title = #{bTitle}, b_content = #{bContent}
 	      WHERE b_id = #{bId}

UPDATE sb_board
SET p_board = #{p_board}, depth = #{depth}, grpord = #{grpord}, b_title = #{bTitle}
WHERE b_id = #{bId}

UPDATE sb_board
SET grpord = grpord + 1
WHERE p_board = #{p_board}
AND grpord > #{grpord}
